sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return; // Exit if the form is loading or the field is empty
    }

   g_form.getReference("destination",callScriptInclude);

    // Get the value of the destination field


    function callScriptInclude(dest) {

        var ga = new GlideAjax('getweatherinfo'); // Instantiate GlideAjax with the Script Include name
        ga.addParam('sysparm_name', 'getDetails'); // Specify the method to call in the Script Include
        ga.addParam('sysparm_city', dest.city); // Pass the destination city as a parameter


        ga.getXMLAnswer(function(response) { // Use getXMLAnswer for a single response and a callback function
        alert(response);
            if (response) {
                var weatherData = response;
                //JSON.parse(); // Parse the JSON response from the Script Include

                // You can now use the weatherData object to update other fields on your form
                g_form.showFieldMsg('destination', 'Weather in ' + weatherData, 'info'); // Example: Display a field message

                // Example: Set other fields based on the weather data
                // g_form.setValue('u_weather_conditions', weatherData.conditions);
                // g_form.setValue('u_temperature', weatherData.temperature);

                // console.log("Weather Data:", weatherData); // For debugging purposes
            } else {
                g_form.showFieldMsg('destination', 'Could not retrieve weather information.', 'error');
                // console.log("Error: No response from Script Include.");
            }
        });
    }


}
