sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

    var startDate = g_form.getValue("start_date");
    var returnDate = g_form.getValue("return_date");

    if (startDate && returnDate) {
        var start = new Date(startDate);
        var end = new Date(returnDate);

        var timeDiff = end.getTime() - start.getTime();
        var days = Math.ceil(timeDiff / (1000 * 3600 * 24)); // Convert ms to days

        if (days >= 0) {
            g_form.setValue("duration", days);
        } else {
            g_form.showFieldMsg("return_date", "Return date cannot be before start date.", "error");
            g_form.setValue("duration", "");
        }
    }
}
