sys_script_include.script:
var getweatherinfo = Class.create();
getweatherinfo.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDetails: function(cityName) {
        var city = cityName ? cityName : this.getParameter("sysparm_city");
        var temp, con, humidity, wind_kph, uv;

        try {
            var r = new sn_ws.RESTMessageV2('x_1804233_right_pr.Get weather Info', 'Default GET');
            r.setStringParameterNoEscape('location', city); // fixed: pass the actual city
            r.setStringParameterNoEscape('key', 'b912559c7d1d4155b9842251231701');

            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();

            if (httpStatus == 200) {
                var jsonobj = JSON.parse(responseBody);

                // Extract weather data
                temp = jsonobj.current.temp_c;
                con = jsonobj.current.condition.text;
                humidity = jsonobj.current.humidity;
                wind_kph = jsonobj.current.wind_kph;
                uv = jsonobj.current.uv;
            }
        } catch (ex) {
            var message = ex.message;
        }

        return "Weather at " + city +
               ": Temperature = " + temp + "Â°C, " +
               "Condition = " + con + ", " +
               "Humidity = " + humidity + "%, " +
               "Wind Speed = " + wind_kph + " kph, " +
               "UV Index = " + uv;
    },

    type: 'getweatherinfo'
});
