sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here

var requestBody = request.body.data;

    var travelGr = new GlideRecord('x_1804233_right_pr_right_prompt_travel_details'); // Replace with actual table name if different
    travelGr.initialize();
    travelGr.from = requestBody.from;
    travelGr.destination = requestBody.destination;
    travelGr.start_date = requestBody.start_date;
    travelGr.return_date = requestBody.return_date;
    travelGr.return = requestBody.return;
    travelGr.caller = requestBody.caller;
    var sysId = travelGr.insert();

    if (sysId) {
        response.setStatus(201); // Created
        return {
            status: 'success',
            message: 'Travel request created successfully',
            sys_id: sysId
        };
    } else {
        response.setStatus(500);
        return {
            status: 'error',
            message: 'Failed to create travel request'
        };
    }


})(request, response);
